import React, { useState, useEffect } from 'react';
import { fetchNews } from '../services/NewService';

const NewsList = () => {
    const [newsItems, setNewsItems] = useState([]);

    useEffect(() => {
        const loadData = async () => {
            const data = await fetchNews();
            setNewsItems(data.items);
        };
        loadData();
    }, []);

    return (
        <div>
            <table>
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Category</th>
                    <th>Title</th>
                    <th>Link</th>
                    <th>Image</th>
                    <th>Date/Time</th>
                </tr>
            </thead>
            <tbody>
                {newsItems.map(item => (
                    <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.category}</td>
                        <td>{item.title}</td>
                        <td><a href={item.link} target="_blank" rel="noreferrer">Link</a></td>
                        <td><img src={item.image} alt={item.title} style={{ width: '100px' }} /></td>
                        <td>{item.datetime_utc}</td>
                    </tr>
                ))}
            </tbody>
        </table>
        </div>
    );
};

export default NewsList;
